import { createApp } from 'vue'; // Importe 'createApp' em vez de 'Vue' no Vue 3
import App from './App.vue';
import { createRouter, createWebHistory } from 'vue-router'; // Importe as funções necessárias do Vue Router

import routes from './router'; // Certifique-se de que o caminho para as rotas esteja correto

const app = createApp(App);
const router = createRouter({
  history: createWebHistory(),
  routes,
});

app.use(router);
app.mount('#app');
